import { gql } from '@apollo/client';

export const getUser = gql`
    query user($username: String!) {
        user(username: $username) {
            errors {
                field
                message
            }
            user {
                id
                username
                avatar
                name
                description
                verified
                followers
                following
            }
        }
    }
`
