import { getUser } from "../../graphql/queries/user";
import { getApolloClient, withApollo } from "../components/hooks/withApolloHook"
import { GetServerSideProps } from "next";
import Nav from '../components/Nav';
import NotFound from '../pages/404'
import styles from '../css/profile/profile.module.css';
import Link from 'next/link';
import Router from 'next/router';

const username = ({ data: { data, loading } }) => {
    if(!data || !data.user || data.user.errors) {
        return <NotFound />
    }

    if(loading) {
      return <p>Loading...</p>
    }

    const { user } = data.user;

    return (
        <>
            <Nav />
            <div className={styles.content}>
              <div className={styles.profile}>
                <div className={styles.information}>                
                  <div className={styles.avatar}>
                    <img src={user ? user.avatar : ''} alt={`${user.name} || ${user.username}`} />
                  </div>
                </div>
                <div className={styles.about}>
                  <h1 className={styles.name}>{user.name}</h1>
                  <h1 className={styles.username}>{user.username}</h1>
                </div>
                <div className={styles.left}>
                  <div className={styles.card}>

                    <div className={styles.card_content}>
                      <div className={styles.statistics}>
                        <Link href="">
                          <span>0</span>
                          <h3>Followers</h3>
                        </Link>
                        <Link href="">
                          <span>0</span>
                          <h3>Following</h3>
                        </Link>
                      </div>
                    </div>


                  </div>
                </div>

              <div className={styles.card_content}>
                <div className={styles.filter}>
                  { process.browser && Router.route === `/${user.username}` ? '' : <p>Button</p>  }
                  <Link href="">
                    <img src="/icons/envelope.png" alt=""/>
                      Message
                  </Link>
                  <button>
                    <img src="/icons/invisible.png" alt=""/>
                  </button>
                  <button>
                    <img src="/icons/flag.png" alt=""/>
                    Report @{user.username}
                  </button>
                </div>
              </div>

              </div>
            </div>
        </>
    )
}

export const getServerSideProps: GetServerSideProps = async (context) => {
    const client = getApolloClient();
    const data = await client.query({
      query: getUser,
      variables: { username: context.params.username as string }
    });
    return {
      props: {
        apolloStaticCache: client.cache.extract(),
        data
      }
    };
}

export default withApollo(username);
