import { useLazyQuery, useQuery } from "@apollo/client"
import { GetServerSideProps } from "next"
import { getUser } from "../../graphql/queries/user";
import { withApollo } from "../components/hooks/withApolloHook"

const username = () => {

    const { data, error, loading } = useQuery(getUser);

    if(loading) {
        return <p>Loading...</p>
    }

    if(error) {
        return <div>{error.message}</div>
    }


    if(!data?.user) {
        <p>sad times man</p>
    }

    return (
        <p>ahoy {data.user}</p>
    )
}

export default withApollo(username);
