import { useQuery } from "@apollo/client"
import { getUser } from "../../graphql/queries/user";
import { getApolloClient, withApollo } from "../components/hooks/withApolloHook"
import { useRouter } from 'next/router';
import { GetServerSideProps } from "next";
import Nav from '../components/Nav';

const username = ({ data: { data, error, errors, loading } }) => {
    if(error) {
      return <p>Error</p>
    }
    
    if(errors) {
      return <p>Error</p>
    }
    
    const styles = {
      margin: "auto",
      width: "50%",
      padding: "10px"
    }

    if(!data || !data.user) {
        return <p>:(</p>
    }

    if(loading) {
      return <p>Loading...</p>
    }
    
    

    return (
        <>
            <Nav />
            <p style={styles}>{data.user.username}</p>
        </>
    )
}

export const getServerSideProps: GetServerSideProps = async (context) => {
    const client = getApolloClient();
    const data = await client.query({
      query: getUser,
      variables: { username: context.params.username as string }
    });
    return {
      props: {
        apolloStaticCache: client.cache.extract(),
        data
      }
    };
}

export default withApollo(username);
