import { useRouter } from "next/router";

import { useAuth } from "../components/hooks/useAuth";
import { getApolloClient, withApollo } from "../components/hooks/withApolloHook";
import Content from "../components/general/profile/home/Home";
import Nav from "../components/Nav"
import styles from "../css/profile/home/content.module.css";
import { useEffect } from "react";
import { meQuery } from "../../graphql/queries/me";
import { gql, useQuery } from "@apollo/client";

export const Home = () => {
    const router = useRouter();
    const client = getApolloClient();

    const me = client.readQuery({
        query: gql`
        query {
            me {
                id
                email
                username
                name
                avatar
                verified
            }
        }
    `
    });

    // const { loading, data } = useQuery(meQuery);

    
    // if(loading) {
    //     return <p>Loading...</p>
    // }

    // if(!data?.me) {
    //     router.push('/login')
    // }

    return (
        <>
            <title>Home</title>
            <Nav/>
            <div className={styles.content}>
                <Content/>
            </div>
        </>
    )
}

export default withApollo(Home);
