import { useQuery } from "@apollo/client"
import { getUser } from "../../graphql/queries/user";
import { getApolloClient, withApollo } from "../components/hooks/withApolloHook"
import { useRouter } from 'next/router';
import { GetServerSideProps } from "next";

const username = ({ loading, error, data }) => {
    // const router = useRouter();
    // const { data, error, loading } = useQuery(getUser, {
    //     variables: { username: 'test' }
    // });

    if(loading) {
        return <p>Loading...</p>
    }

    if(error) {
        return <div>{error.message}</div>
    }


    if(!data?.user) {
        <p>sad times man</p>
    }

    return (
        <p>{JSON.stringify(data.user)}</p>
    )
}

export const getServerSideProps: GetServerSideProps = async (context) => {
    const client = await getApolloClient();
    const { loading, error, data } = await client.query({
      query: getUser,
      variables: { username: context.params.username as string }
    });
    return {
      props: {
        apolloStaticCache: client.cache.extract(),
        error,
        data,
        loading
      }
    };
}

export default withApollo(username);
