import { getUser } from "../../graphql/queries/user";
import { getApolloClient, withApollo } from "../components/hooks/withApolloHook"
import { GetServerSideProps } from "next";
import Nav from '../components/Nav';
import NotFound from '../pages/404'
import styles from '../css/profile/profile.module.css';
import Link from 'next/link';
import { useRouter } from 'next/router';
import { useAuth } from "../components/hooks/useAuth";

const username = ({ data: { data, loading } }) => {
    if(!data || !data.user || data.user.errors) {
        return <NotFound />
    }

    if(loading) {
      return <p>Loading...</p>
    }

    const { user } = data.user;
    let { me } = useAuth();
    const router = useRouter();
    while(me === undefined) {
      continue;
    }
    console.log(me)
    // console.log(user)
    // if(me.username === user.username) console.log(true)

    return (
        <>
            <Nav />
            <div className={styles.content}>
              <div className={styles.profile}>
                <div className={styles.information}>                
                  <div className={styles.avatar}>
                    <img src={user ? user.avatar : ''} alt={`${user.name} || ${user.username}`} />
                  </div>
                </div>
                <div className={styles.about}>
                  <h1 className={styles.name}>{user.name}</h1>
                  <h1 className={styles.username}>{user.username}</h1>
                </div>
                <div className={styles.left}>
                  <div className={styles.card}>

                    <div className={styles.card_content}>
                      <div className={styles.statistics}>
                        <Link href=""><a href="">
                          <span>0</span>
                          <h3>Followers</h3>
                        </a></Link>
                        <Link href=""><a href="">
                          <span>0</span>
                          <h3>Following</h3>
                        </a></Link>
                      </div>
                    </div>


                  </div>
                </div>

              <div className={styles.right}>
              <div className={styles.card}>
                <div className={styles.card_content}>
                  <div className={styles.filter}>
                    {/* { process.browser && router.route === `/${me.username}` ? '' : <p>Button</p>  } */}
                    <Link href=""><a href="">
                      <img src="/icons/envelope.png" alt=""/>
                        Message
                    </a></Link>
                    <button>
                      <img src="/icons/invisible.png" alt=""/>
                      Block
                    </button>
                    <button>
                      <img src="/icons/flag.png" alt=""/>
                      Report @{user.username}
                    </button>
                  </div>
                </div>
                </div>
              </div>
            </div>
            </div>

            <div className={styles.mobile}>
              <div className={styles.mobile_profile}>
                <div className={styles.mobile_profile_navigation}>
                  <Link href="/home">
                    <a href="/home" className={styles.mobile_page_explore}>
                  </a></Link>
                    <h1 className={styles.mobile_profile_username}>@{user.username}</h1>
                    <button className={styles.mobile_page_settings}></button>
                </div>
                <div className={styles.mobile_information}>
                  <div className={styles.mobile_images}>
                    <img className={styles.mobile_avatar} src={user ? user.avatar : ''} alt={`${user.name} || ${user.username}`}/>
                    <section className={styles.mobile_statistics}>
                      <a href="">
                        <span>0</span>
                        <h3>Followers</h3>
                      </a>
                      <a href="">
                        <span>0</span>
                        <h3>Following</h3>
                      </a>
                    </section>
                  </div>
                  <div className={styles.mobile_interactions}>
                    {/* { router.route === `${user.username}` ? '' : <p>Button</p> } */}
                    <Link href={`/message/${user.username}`}><a href={`/message/${user.username}`} className={styles.mobile_messageUser}>Message</a></Link>
                  </div>
                </div>

                <div className={styles.mobile_filter}>
                  <a href="" className={styles.mobile_filter_profile}></a>
                  <a href="" className={styles.mobile_filter_shares}></a>
                  {/* <a href="" className={styles.mobile_filter_connections}></a> */}
                  <a href="" className={styles.mobile_filter_media}></a>                  
                </div>
                
              </div>
            </div>
        </>
    )
}

export const getServerSideProps: GetServerSideProps = async (context) => {
    const client = getApolloClient();
    const data = await client.query({
      query: getUser,
      variables: { username: context.params.username as string }
    });
    return {
      props: {
        apolloStaticCache: client.cache.extract(),
        data
      }
    };
}

export default withApollo(username);
